
public class Ifelse {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=5;
		int b=6;
		if(a<b)
		{
			System.out.println("a is less than b");
		}
		else if(a>b)
		{
			System.out.println("a is greaterthan b");
		}
		else
		{
			System.out.println("a is equal to b");
		}
		

	}

}
