
public class Shorthandoper {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=9;
		i += 2; //i=i+2;
		System.out.println("Sum is:"+i);
		i *=4;
		System.out.println("Multiplication is:"+i);
		i /=3;
		System.out.println("Division is:"+i);

	}

}
